response = Unirest.post(
                        "http://localhost:3000/api/cars"
                        parameters: {
                                    make: "Toyota",
                                    model: "Prius",
                                    price: "25000.00",
                                    image_url: "data:image/jpeg;base64,"
                                    }
                        )
puts JSON.pretty_generate(response.body)

car_id = 5
runner_params = {
                model: "86"
                }
response = Unirest.patch(
                        "http://localhost:3000/api/cars/#{car_id}",
                        parameters: runner_params
                        )                
car_hash = response.body
puts JSON.pretty_generate(car_hash)

car_id = 5
response = Unirest.delete("http:localhost:3000/api/cars/#{car_id}")
data = response.body
puts JSON.pretty_generate(data)










